         SetBoxs( prevBoxs => {
        return prevBoxs.map(box => {

           return box.id === id ? {...box, on:!box.on} : box

        })} )}

    const listBox =  boxs.map(item =>{


        return(     <Box toogle={toogle} on={item.on} className="box12" key={item.id} id={item.id }></Box>)})   


